INSERT INTO script_texts (entry,content_default,sound,type,language,emote,comment) VALUES
(-1585023,'Don\'t look so smug! I know what you\'re thinking, but Tempest Keep was merely a set back. Did you honestly believe I would trust the future to some blind, half-night elf mongrel?',0,1,0,0,'kaelthas MT SAY_INTRO_1'),
(-1585030,'Oh no, he was merely an instrument, a stepping stone to a much larger plan! It has all led to this, and this time, you will not interfere!',0,1,0,0,'kaelthas MT SAY_INTRO_2');
